print("Question 1: \n")
# Question 1

print("Give me 2 numbers between 1-999: ")
while 1:
    num1 = int(input("Enter the first number:"))
    num2 = int(input("Enter the second number:"))
    if 1 <= num1 <= 999 and 1 <= num2 <= 999:
        if num1 <= num2:
            print("The first number must be greater than the second one.")
        else:
            print(words[num2:num1])
            break
    else:
        print("Numbers must be between 1 and 999")
print("Question 2: \n")
# Question 2

while 1:
    user_number = int(input("Enter the number of words in your password between 3 and 7: "))
    if 3 <= user_number <= 7:
        password = ""
        for i in range(user_number):
            random_index = random.randint(0, len(words) - 1)
            password += words[random_index] + ""
        password_length = len(password)
        reversed_password = ''
        for i in range(password_length - 1, -1, -1):
            reversed_password += password[i]
        print("Your password is:", reversed_password)
        break
    else:
        print("Number of words must be between 3 and 7.")
print("Question 3: \n")
# Question 3

shorter = set()
longer = set()

for i in range(8):
    while True:
        user_number = int(input("Enter the number of words in your password between 3 and 7: "))
        if 3 <= user_number <= 7:
            password = ""
            for i in range(user_number):
                random_index = random.randint(0, len(words) - 1)
                password += words[random_index] + ""
            reversed_password = password[::-1]
            print("Your password is:", reversed_password)
            if user_number < 5:
                shorter.add(reversed_password)
            else:
                longer.add(reversed_password)
            break
        else:
            print("Number of words must be between 3 and 7.")
            continue

print("Combined passwords:", shorter.union(longer))

