print("Question 4d: \n")
# Question 4d

dict4 = {}
for i in range(1, 31):
    dict4[i] = i * (i-1)
while 1:
    number = input("Enter a number between 1 and 30 to remove the item from the dictionary: ")

    if number.isdigit():
        number = int(number)
        if 1 <= number <= 30:
            if number in dict4:
                del dict4[number]
                print(f"The item with key {number} has been removed from the dictionary.")
                break
            else:
                print(f"The entered number {number} is not a key in the dictionary.")
        else:
            print("The entered number is outside the range of 1 to 30.")
    else:
        print("Invalid input. Please enter a valid integer between 1 and 30.")


print("Question 5a: \n")
# Question 5a

divided = {'Tony': 41, 'Rhodey': 43, 'Nat': 35}
we_fall = {'Steve': 39, 'Clint': 35, 'Wanda': 28}

united_we_stand = divided.copy()
united_we_stand.update(we_fall)

print("Name\tAge")
for name, age in united_we_stand.items():
    print(f"{name}\t{age}")


print("Question 5b: \n")
# Question 5b

del united_we_stand['Nat']

print("After removing 'Nat':")
print("Name\tAge")
for name, age in united_we_stand.items():
    print(f"{name}\t{age}")

print("Question 5c: \n")
# Question 5c

sorted_keys = list(united_we_stand.keys())
sorted_keys.sort()
print("Name\tAge")
for name in sorted_keys:
    age = united_we_stand[name]
    print(f"{name}\t{age}")

print("Question 5d: \n")
# Question 5d

values = list(united_we_stand.values())
max_value = values[0]
min_value = values[0]
for age in values[1:]:
    if age > max_value:
        max_value = age
    if age < min_value:
        min_value = age
print(f"Maximum value: {max_value}")
print(f"Minimum value: {min_value}")